import React, { useState, useEffect, useCallback } from 'react';

// --- Helper Components ---

// Icon for delete button
const TrashIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-gray-400 group-hover:text-red-500 transition-colors duration-200">
    <polyline points="3 6 5 6 21 6"></polyline>
    <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path>
    <line x1="10" y1="11" x2="10" y2="17"></line>
    <line x1="14" y1="11" x2="14" y2="17"></line>
  </svg>
);

// Loading spinner component
const Spinner = () => (
    <div className="flex justify-center items-center p-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-500"></div>
    </div>
);

// --- Main Home Component ---

export default function Home() {
  // --- State Management ---
  const [expenses, setExpenses] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('Food');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- API URL ---
  const API_URL = 'http://127.0.0.1:5000/api/expenses';

  // --- Data Fetching ---
  const fetchExpenses = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setExpenses(data);
    } catch (e) {
      setError('Failed to fetch expenses. Please make sure the backend server is running.');
      console.error(e);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchExpenses();
  }, [fetchExpenses]);

  // --- Event Handlers ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Clear previous errors on a new submission attempt
    setError(null); 
    
    if (!description || !amount || !category) {
        // Use the state for displaying errors instead of alert()
        setError('Please fill all fields.');
        return;
    }

    const newExpense = {
        description,
        amount: parseFloat(amount),
        category,
    };

    try {
        const response = await fetch(API_URL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newExpense),
        });

        if (!response.ok) {
            throw new Error('Failed to add expense');
        }

        // Reset form and refetch expenses to show the new one
        setDescription('');
        setAmount('');
        setCategory('Food');
        fetchExpenses(); 

    } catch (e) {
        setError('Failed to add expense.');
        console.error(e);
    }
  };
  
  const handleDelete = async (id) => {
    try {
        const response = await fetch(`${API_URL}/${id}`, {
            method: 'DELETE',
        });
        if (!response.ok) {
            throw new Error('Failed to delete expense');
        }
        // Refetch expenses to update the list
        fetchExpenses();
    } catch (e) {
        setError('Failed to delete expense.');
        console.error(e);
    }
  };


  // --- Render Logic ---
  const totalExpenses = expenses.reduce((total, expense) => total + expense.amount, 0).toFixed(2);

  return (
    <div className="bg-gray-50 min-h-screen font-sans text-gray-800">
      <div className="container mx-auto p-4 sm:p-6 lg:p-8 max-w-4xl">
        
        {/* Header */}
        <header className="text-center mb-8">
          <h1 className="text-4xl font-bold text-indigo-600">Smart Expense Tracker</h1>
          <p className="text-gray-500 mt-2">Take control of your finances with ease.</p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          
          {/* Left Column: Add Expense Form & Summary */}
          <div className="md:col-span-1 space-y-8">
            {/* Add Expense Form */}
            <div className="bg-white p-6 rounded-xl shadow-md">
              <h2 className="text-2xl font-semibold mb-4 border-b pb-2">Add New Expense</h2>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                  <input
                    type="text"
                    id="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="e.g., Coffee with friends"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
                <div>
                  <label htmlFor="amount" className="block text-sm font-medium text-gray-700 mb-1">Amount ($)</label>
                  <input
                    type="number"
                    id="amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    placeholder="e.g., 4.50"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                  <select
                    id="category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white"
                  >
                    <option>Food</option>
                    <option>Transport</option>
                    <option>Utilities</option>
                    <option>Entertainment</option>
                    <option>Other</option>
                  </select>
                </div>
                <button type="submit" className="w-full bg-indigo-600 text-white font-bold py-2 px-4 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-transform transform hover:scale-105">
                  Add Expense
                </button>
              </form>
            </div>

            {/* Summary */}
            <div className="bg-white p-6 rounded-xl shadow-md text-center">
                 <h3 className="text-lg font-semibold text-gray-600">Total Expenses</h3>
                 <p className="text-3xl font-bold text-indigo-600 mt-2">${totalExpenses}</p>
            </div>
          </div>

          {/* Right Column: Expense List */}
          <div className="md:col-span-2 bg-white p-6 rounded-xl shadow-md">
            <h2 className="text-2xl font-semibold mb-4 border-b pb-2">Expense History</h2>
            {error && <p className="text-red-500 bg-red-100 p-3 rounded-lg mb-4">{error}</p>}
            {loading ? (
              <Spinner />
            ) : (
              <div className="space-y-4">
                {expenses.length > 0 ? (
                  [...expenses].reverse().map((expense) => (
                    <div key={expense.id} className="flex items-center justify-between bg-gray-50 p-4 rounded-lg hover:bg-gray-100 transition-colors duration-200">
                      <div className="flex items-center space-x-4">
                        <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold ${
                            expense.category === 'Food' ? 'bg-green-500' :
                            expense.category === 'Transport' ? 'bg-blue-500' :
                            expense.category === 'Utilities' ? 'bg-yellow-500' :
                            expense.category === 'Entertainment' ? 'bg-purple-500' : 'bg-gray-500'
                        }`}>
                          {expense.category.charAt(0)}
                        </div>
                        <div>
                          <p className="font-semibold">{expense.description}</p>
                          <p className="text-sm text-gray-500">{expense.category}</p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        <p className="font-bold text-lg text-red-500">-${expense.amount.toFixed(2)}</p>
                        <button onClick={() => handleDelete(expense.id)} className="p-2 rounded-full hover:bg-gray-200 group">
                            <TrashIcon />
                        </button>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-gray-500 text-center py-8">No expenses recorded yet. Add one to get started!</p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
